import * as React from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { IconButton, Button, CardActionArea, CardActions } from '@mui/material';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import useMediaQuery from '@mui/material/useMediaQuery';
import MailIcon from '@mui/icons-material/Mail';
import { useTheme } from '@mui/material/styles';
import EmailModal from './EmailModal';

export default function TeamMember({member}) {
    const [scroll, setScroll] = React.useState('paper');
    const [openBio, setOpenBio] = React.useState(false);
    const [openEmail, setOpenEmail] = React.useState(false);
  const theme = useTheme();

const handleClickOpenEmail = () => {
    setOpenEmail(true);
}
  const handleClickOpenBio = (scrollType) => () => {
    setOpenBio(true);
    setScroll(scrollType);
  };

  const handleCloseBio = () => {
    setOpenBio(false);
  };

  const descriptionElementRef = React.useRef(null);
  React.useEffect(() => {
    if (openBio) {
      const { current: descriptionElement } = descriptionElementRef;
      if (descriptionElement !== null) {
        descriptionElement.focus();
      }
    }
  }, [openBio]);

    return (
        <>
    <Card sx={{ maxWidth: 345 }}>
      <CardActionArea onClick={handleClickOpenBio('paper')}>
        <CardMedia
          component="img"
          height="140"
          image={require('../images/biglogo.png')}
          alt={member.name}
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="div">
            {member.name}
          </Typography>
          <Typography>
            {member.role}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            {member.bio}
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
        <IconButton onClick={handleEmail()}>
            <MailIcon/>
        </IconButton>
      </CardActions>
    </Card>
    <Dialog
        open={openBio}
        onClose={handleCloseBio}
        scroll={scroll}
        aria-labelledby="scroll-dialog-title"
        aria-describedby="scroll-dialog-description"
      >
        <DialogTitle id="scroll-dialog-title">Bio: {member.name}</DialogTitle>
        <DialogContent dividers={scroll === 'paper'}>
          <DialogContentText
            id="scroll-dialog-description"
            ref={descriptionElementRef}
            tabIndex={-1}
          >
            {[...new Array(50)]
              .map(
                () => `Cras mattis consectetur purus sit amet fermentum.
Cras justo odio, dapibus ac facilisis in, egestas eget quam.
Morbi leo risus, porta ac consectetur ac, vestibulum at eros.
Praesent commodo cursus magna, vel scelerisque nisl consectetur et.`,
              )
              .join('\n')}
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseBio}>OK</Button>
        </DialogActions>
      </Dialog>
  </>
  );
}
